import React from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import JobResult from "./JobResult";
import uniqid from "uniqid";
import { Link } from "react-router-dom";

import { fetchJobs } from "../store/actions";
import { useSelector,useDispatch } from "react-redux";
import { useState,useEffect } from "react";


const MainSearch =()=> {

const {query, setQuery}= useState("")
//const {jobs,setJobs}= useState([])

const jobs = useSelector(state=>state.jobs.elememts)

const dispatch = useDispatch()
 const  baseEndpoint = "https://strive-jobs-api.herokuapp.com/jobs?search=";

  const handleChange = (e) => {
    setQuery(e.target.value ) 
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(fetchJobs(baseEndpoint, query))
  };

  useEffect(()=>{
    setJobs(fetchJobs(baseEndpoint, query))
  },[])


    return (
      <Container>
        <Row>
          <Col xs={10} className="mx-auto my-3">
            <h1>Remote Jobs Search</h1>
            <Link to="/favourites" className="btn btn-primary">
              Favourites
            </Link>
          </Col>
          <Col xs={10} className="mx-auto">
            <Form onSubmit={handleSubmit}>
              <Form.Control
                type="search"
                value={query}
                onChange={handleChange}
                placeholder="type and press Enter"
              />
            </Form>
          </Col>
          <Col xs={10} className="mx-auto mb-5">
            {jobs.map((jobData) => (
              <JobResult key={uniqid()} data={jobData} />
            ))}
          </Col>
        </Row>
      </Container>
    );
  }


export default MainSearch
